UpVote.destroy_all
IdeaTag.destroy_all
Implementor.destroy_all
Comment.destroy_all
Idea.destroy_all
User.destroy_all
Tag.destroy_all

user1 = User.create(name: "name1", email: "email1@email.com", image: "data:image/png;base64,")
user2 = User.create(name: "name2", email: "email2@email.com", image: "data:image/png;base64,")
user3 = User.create(name: "name3", email: "email3@email.com", image: "https://encrypted-tbn0.gstatic.com/images?q=tbn%3AANd9GcRbrfCu4dGpQmGXER5Gp1uhmwue7SGZe7degvfAnGPqyAW1tnM6")
tag = Tag.create(name: "test")
idea = Idea.create(user: user1, title: "title", description: "description", image: "data:image/png;base64,")
IdeaTag.create(idea: idea, tag: tag)
Implementor.create(idea: idea, user: user2)
Comment.create(user: user3, idea: idea, content: "comment", by_participant: true)
UpVote.create(user: user3, idea: idea)

